import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import { getAllCategory } from '../../Redux/actions/CategoryAction'
import { createsubCategory } from '../../Redux/actions/SubCategoryAction';

const AdminAddSubcategory = () => {

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getAllCategory())
    }, [])


    const [Id, setId] = useState(0)
    const [name, setname] = useState('')
    const [Loading, setLoading] = useState(true)

    const category = useSelector(state => state.allCategory.category)


    const handelChange = (e)=> {
        console.log(e.target.value)
        setId(e.target.value)
    }

    // Massage 
    const [ErrorMass, setErrorMass] = useState("")


    const handelSubmit = async (e)=> {
        e.preventDefault()
        if(Id === 0){
            setErrorMass("Please enter the category")
        }
        if(name === ''){
            setErrorMass('Please enter the name')
        }

        setLoading(true)
        await dispatch(createsubCategory({
            name:name,
            category:Id
        }))
        setLoading(false)

    }

    useEffect(() => {
      if (Loading === false){
        setname("")
        setId(0)
      }
    }, [Loading])
    

    return (
        <div className='AdminAddSubcategory'>
            <div className='Subcategory-input'>
                <input value={name} onChange={(e) => setname(e.target.value)} placeholder='Subcategory name' />
            </div>
            <select id="order" onChange={handelChange}>
                <option value="0">Add classification</option>


                {
                    category.data ? (category.data.map((item) => {
                     return   <option key={item._id} value={item._id}>{item.name}</option>
                    })) : null
                }

            </select>
            <div style={{color:"red", marginTop:"10px"}}>{ErrorMass}</div>
            <div>
                <button onClick={handelSubmit} className='Subcategory-button'> Save Edit</button>
            </div>
        </div>
    )
}

export default AdminAddSubcategory
