import React, { useState, useEffect } from 'react'
import avatar from '../../images/avatar.png'
import { useSelector, useDispatch } from 'react-redux'
import { createCategory } from '../../Redux/actions/CategoryAction'
const AdminAddCategory = () => {

    const dispatch = useDispatch();

    const [img, setimg] = useState(avatar)
    const [name, setname] = useState('')
    const [Selectfile, setSelectfile] = useState(null)
    const [Loading, setLoading] = useState(true)
    const [IsPress, setIsPress] = useState(false)
    const [foundError, setfoundError] = useState("")

    //When image change save it 
    const onImageChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setimg(URL.createObjectURL(event.target.files[0]))
            setSelectfile(event.target.files[0])
        }

    }
    const vv = foundError
    //save data in database 
    const handelSubmit = async (event) => {
        event.preventDefault();

        if(name === "" || Selectfile === null){
            setfoundError("Complete the requirements")
        }

        const formData = new FormData();
        formData.append("name", name)
        formData.append("image", Selectfile)

        setLoading(true)
        setIsPress((true))
        await dispatch(createCategory(formData))
        setTimeout(() => setIsPress(false), 3000)
    }

    useEffect(() => {
        if (Loading === false) {
            setimg(avatar)
            setname("")
            setSelectfile(null)
            console.log("finsh")
            setLoading(true)
            setIsPress(false)
        }
    }, [Loading])

    return (
        <div className='AdminAddBrand'>
            <div className='img-add-brand'>
                <div>
                    <label htmlFor="upload-photo">
                        <img
                            src={img}
                            alt="fzx"
                            height="120px"
                            width="120px"
                            style={{ cursor: "pointer" }}
                        />
                    </label>
                    <input
                        type="file"
                        name="photo"
                        onChange={onImageChange}
                        id="upload-photo"
                    />
                </div>
            </div>
            <div className='input-add-brand'>
                <input
                    onChange={(e) => setname(e.target.value)}
                    value={name}
                    placeholder='Name Category' />
            </div>
            <div>{foundError}</div>
            <div className='button-add-brand'>
                <button onClick={handelSubmit} >Save Edit</button>
            </div>

            {
                IsPress ? Loading ? <div className="text-center" style={{ width: "100%" }}>
                    <div className="spinner-border" role="status">
                        <span className="sr-only">Loading...</span>
                    </div>
                </div> : <h4>Done</h4> : null
            }
        </div>
    )
}

export default AdminAddCategory
