                {
                    category.data ? (category.data.map((item) => {
                     return   <option key={item._id} value={item._id}>{item.name}</option>
                    })) : null
                }

            </select>
            <div>
                <button className='Subcategory-button'>ŸçSave Edit</button>
            </div>
        </div>
    )
}

