import { CREATE_SUBCATEGORY, ERROR } from "../Type";
import { axios } from "axios";


export const subCategory = (Data) => async (dispatch) => {

    try {
        const response = await axios.post(`/api/v1/subcategories`, Data);
        dispatch({
            type: CREATE_SUBCATEGORY,
            payload: response,
            loading: true
        })

    } catch (e) {
        dispatch({
            type: ERROR,
            payload: "Error " + e,
        })
    }
}
