import { GET_ALL_GATEGORY, ERROR } from "../Type";
import useGetData from "../../Hook/useGetData";



export const getAllCategory = async (dispatch) => {
    try {
        const respons = await useGetData.get('/api/v1/categories');
        dispatch({
            type: GET_ALL_GATEGORY,
            payload: respons.data,
        })

    } catch (e) {
        dispatch({
            type: ERROR,
            payload: "ERROR" + e,
        })
    }
}
