import React, { useEffect } from 'react'
import clothe from '../../images/clothe.png'
import cat2 from '../../images/cat2.png'
import laptop from '../../images/labtop.png'
import sale from '../../images/sale.png'
import pic from '../../images/pic.png'
import CategoryCard from './CategoryCard'
import { useSelector, useDispatch } from 'react-redux'
import { getAllCategory } from '../../Redux/actions/CategoryAction'
const CategoryComponent = () => {

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getAllCategory)
    }, [])

    const category = useSelector(state => state.allCategory.category)
    const loading = useSelector(state => state.allCategory.loading)

    const image = [clothe, cat2, laptop, clothe, sale, pic, laptop]

    return (
        <div>
            <div className='home-category container'>
                <div> All Category</div>
                <div className='home-category-card container'>

                    {
                        category.data ? (
                            category.data.map((item, index) => {
                                return <CategoryCard title={item.name} img={image[Math.floor(Math.random() * 9)]} />
                            })
                        ) : null
                    }


                </div>
            </div>
        </div>
    )
}

export default CategoryComponent
