import { GET_ALL_GATEGORY } from '../Type'


const inital = {
    category: [],
    loading: true
}

const categoryReduser = (state = inital, action) => {
    switch (action.type) {
        case GET_ALL_GATEGORY:
            return {
                ...state,
                category: action.payload,
                loading: false,
            }
        default:
            return state;
    }
}

export default categoryReduser
