import React, { useState } from 'react'
import notify from '../UseNotifaction';
import { useDispatch, useSelector } from 'react-redux';
import { createNewUcer } from '../../Redux/actions/AuthAction';

const LogInHook = () => {

    const dispatch = useDispatch()

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [loading, setLoading] = useState(true)


    const onChangeName = (e) => {
        setName(e.target.value)
    }
    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    }
    const onChangePasswprd = (e) => {
        setPassword(e.target.value)
    }
    const onChangeconfirmPassword = (e) => {
        setConfirmPassword(e.target.value)
    }


    const ValidationValues = () => {
        if (name === "") {
            notify("Enter Name", "error")
            return;
        }
        if (email === "") {
            notify("Enter Email", "error")
            return;
        }
        if (password === "") {
            notify("Enter Password", "error")
            return;
        }
        if (confirmPassword === "") {
            notify("ُEnter Confirm Password", "error")
            return;
        }
    }


    const OnSubmit = async () => {
        ValidationValues();
        setLoading(tru)
       await dispatch(createNewUcer({
            name,
            email,
            password,
            passwordConfirm : confirmPassword,
        }))
        

    }
    return [name, email, password, confirmPassword, loading, onChangeName, onChangeEmail, onChangePasswprd, onChangeconfirmPassword,
        ValidationValues, OnSubmit]
}

export default LogInHook
