import { useState, useEffect } from 'react'
import notify from '../UseNotifaction';
import { useDispatch, useSelector } from 'react-redux';
import { createNewUser, logInUser } from '../../Redux/actions/AuthAction';
import { useNavigate } from 'react-router-dom';
const LogInHook = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [loading, setLoading] = useState(true)

    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    }
    const onChangePasswprd = (e) => {
        setPassword(e.target.value)
    }

    const onsubmite = async () => {
        await dispatch(logInUser({
            email,
            password
        }))
    }
    const res = useSelector(state => state.AuthReducer.logInUser)
    useEffect(() => {
        if (loading === false) {
            if (res) {
                console.log(res)
                if (res.data.token) {
                    localStorage.setItem("token", res.data.token)
                    localStorage.setItem("user", JSON.stringify(res.data.data))
                    notify("You are logged in successfully", "success")
                    setTimeout(() => {
                        window.location.href = "/"
                    }, 1500);
                } else {
                    localStorage.removeItem("token")
                    localStorage.removeItem("user")
                }

                if (res.data.message === "Incorrect email or password") {
                    localStorage.removeItem("token")
                    localStorage.removeItem("user")
                    notify("Wrong password or email","error")
                }
                setLoading(true)
            }
        }
    }, [loading])
    return [email, password, onChangeEmail, onChangePasswprd, onsubmite]
}

export default LogInHook
