import { GET_ALL_GATEGORY, ERROR } from "../Type";
import baseUrl from '../../Api/baseURL';
import useGetData from "../../Hook/useGetData";
export const getAllCategory = (limit) => async (dispatch) => {
    try {

        const resp = await baseUrl.get(`/api/v1/categories?limit=${limit}`)
        dispatch({
            type: GET_ALL_GATEGORY,
            payload: res.data,
        })

    } catch (e) {
        dispatch({
            type: ERROR,
            payload: "ERROR" + e,
        })
    }
}


export const getAllCategoryPage = (Page) => async (dispatch) => {
    try {

        const res = await baseUrl.get(`/api/v1/categories?limit=4&page=${Page}`)
        dispatch({
            type: GET_ALL_GATEGORY,
            payload: res.data,
        })

    } catch (e) {
        dispatch({
            type: ERROR,
            payload: "ERROR" + e,
        })
    }
}
