import React, { useState, useEffect } from 'react'
import notify from '../UseNotifaction';
import { useDispatch, useSelector } from 'react-redux';
import { createNewUser } from '../../Redux/actions/AuthAction';

const LogUpHook = () => {

    const dispatch = useDispatch()

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [passwordConfirm, setpasswordConfirm] = useState('')
    const [loading, setLoading] = useState(true)


    const onChangeName = (e) => {
        setName(e.target.value)
    }
    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    }
    const onChangePasswprd = (e) => {
        setPassword(e.target.value)
    }
    const onChangepasswordConfirm = (e) => {
        setpasswordConfirm(e.target.value)
    }


    const ValidationValues = () => {
        if (name === "") {
            notify("Enter Name", "error")
            return;
        }
        if (email === "") {
            notify("Enter Email", "error")
            return;
        }
        if (password === "") {
            notify("Enter Password", "error")
            return;
        }
        if (passwordConfirm === "") {
            notify("ُEnter Confirm Password", "error")
            return;
        }
    }
    const res = useSelector(state => state.AuthReducer.createUser)

    // Save Data
    const OnSubmit = async () => {
        ValidationValues();
        setLoading(true)
        await dispatch(createNewUser({
            name,
            email,
            password,
            passwordConfirm,
        }))
       
        setLoading(false)
        consol
    }

    useEffect(() => {
        if (loading === false) {
            if (res) {
                console.log(res)
              //  if (res.data.token) {
                   // localStorage.setItem("token", res.data.token)
                   // notify("تم تسجيل الحساب بنجاح", "success")
                   
              //  }
            }
        }
    }, [loading])

    return [name, email, password, passwordConfirm, loading, onChangeName, onChangeEmail, onChangePasswprd, onChangepasswordConfirm,
        ValidationValues, OnSubmit]
}

export default LogUpHook
