import { CREATE_NEW_USER ,LOG_IN_USER } from './../Type';


const inital = {
    createUser: [],
    loading: true,
}

const AuthReducer = (state = inital, action) => {
    switch (action.type) {
        case CREATE_NEW_USER:
            return {
                ...state,
                createUser: action.payload,
                loading: false,
                
            }
            caseLOG_IN_USER
        default: return state;
    }
}

export default AuthReducer
